import React, {Component} from 'react';
import Navigation from './components/Navigation/Navigation';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import SignIn from './components/SignIn/SignIn';
import Register from './components/Register/Register';
import Particles from 'react-particles-js';
import './App.css';
import { connect } from 'react-redux';

import { 
  addFaceBox,
  changeInput,
  updateUserInfo,
  updateImageUrl,
  updateSignIn,
  updateRoute } from './actions';

const particleOptions = {
  particles: {
    number: {
      value: 150,
      density: {
        enable: true,
        value_area: 800
      }
      }
    }
}

const initialState = {
      // input: '',
      // imageUrl: '',
      // box: {},
      route: 'signin',
      isSignedIn: false
      // user: {
      //   id: '',
      //   name: '',
      //   email: '',
      //   entries: 0,
      //   joined: ''
      // }
}

const mapStateToProps = state => {
  return {
    box: state.addFaceBox.box,
    input: state.inputStateChange.input,
    user: state.userInfoUpdate.user,
    imageUrl: state.imageUrlUpdate.imageUrl,
    isSignedIn: state.signInUpdate.isSignedIn,
    route: state.routeUpdate.route
  }
}
const mapDispatchToProps = (dispatch) => {
  return {
    displayFaceBox: (box) => dispatch(addFaceBox(box)),
    onInputChange: (event) => dispatch(changeInput(event.target.value)),
    loadUser: (data) => dispatch(updateUserInfo(data)),
    onImageUrlInput: (url) => dispatch(updateImageUrl(url)),

  }
}

class App extends Component {

  constructor() {
    super()
    this.state = initialState
  }

  // loadUser = (data) => {
  //   this.setState({user: {
  //       id: data.id,
  //       name: data.name,
  //       email: data.email,
  //       entries: data.entries,
  //       joined: data.joined
  //   }})
  // }

  // componentDidMount() {
  //   console.log(this.props.store.getState())
  // }

  // componentDidMount() {
  //   fetch('http://localhost:3000/')
  //     .then(response => response.json())
  //     //.then(data => console.log(data))
  //     .then(console.log)
  // }

  calculateFaceLocation = (data) => {
    console.log(data);
    const clarifaiFace = data.outputs[0].data.regions[0].region_info.bounding_box;
    const image = document.getElementById('inputimage');
    const width = Number(image.width);
    const height = Number(image.height);
    return {
      leftCol: clarifaiFace.left_col * width,
      topRow: clarifaiFace.top_row * height,
      rightCol: width - (clarifaiFace.right_col * width),
      bottomRow: height - (clarifaiFace.bottom_row * height)
    }
  }

  // displayFaceBox = (box) => {
  //   // console.log(box);
  //   this.setState({box});
  // }

  // onInputChange = (event) => {
  //   // console.log(event.target.value);
  //   this.setState({input: event.target.value})
  // }

  onButtonSubmit = () => {
    const { displayFaceBox, input, onImageUrlInput, loadUser, user } = this.props;
    onImageUrlInput(input);
    // this.setState({imageUrl: input});
    // console.log('click');
    fetch(' https://boiling-sea-92403.herokuapp.com/imageurl', {
      method: 'post',
      headers: {'Content-Type': 'application/json'},
      body: JSON.stringify({
        input: input
      })
    })
    .then(response => response.json())
    .then(response => {
      // console.log(response)
      // console.log(response.outputs[0].data.regions[0].region_info.bounding_box);
      if (response) {
        fetch(' https://boiling-sea-92403.herokuapp.com/image', {
          method: 'put',
          headers: {'Content-Type': 'application/json'},
          body: JSON.stringify({
            id: user.id
          })
        })
        .then(response => response.json())
        .then(count => {
          loadUser(count)
          // this.setState(Object.assign(this.state.user, {entries: count}))
          // this.setState({user: {
          //   entries: count
          // }})
        })
        .catch(console.log)
      }
      displayFaceBox(this.calculateFaceLocation(response))
    })
    .catch(err => console.log(err));
  }

  onRouteChange = (route) => {
    if (route === 'signout') {
      this.setState(initialState)
    } else if (route === 'home') {
      this.setState({isSignedIn: true})
    }
    this.setState({route: route})
  }

  render() {
    const { isSignedIn, route} = this.state;
    const { box, onInputChange, loadUser, user, imageUrl } = this.props;
    return (
      <div className="App">
      <Particles className='particles'
        params={particleOptions} />
        <Navigation onRouteChange={this.onRouteChange} isSignedIn={isSignedIn}/>
        { route === 'home'
        ? <div>
            <Logo />
            <Rank name={user.name} entries={user.entries}/>
            <ImageLinkForm
              onInputChange={onInputChange}
              onButtonSubmit={this.onButtonSubmit}
            /> 
            <FaceRecognition box={box} imageUrl={imageUrl}/>
          </div>
        : (
            this.state.route === 'signin' 
            ? <SignIn 
                onRouteChange={this.onRouteChange}
                loadUser={loadUser}
              />
            : <Register
                onRouteChange={this.onRouteChange}
                loadUser={loadUser}
              />
          )

        }
      </div>
    );
  }
}
export default connect(mapStateToProps, mapDispatchToProps)(App);
