import {
	ADD_FACE_BOX,
	ON_INPUT_CHANGE,
	LOAD_DATA,
	UPDATE_DATA,
	URL_ENTERED
} from './constants.js'

const initialStateBox = {
	box: {}
}

export const addFaceBox = (state=initialStateBox, action={}) => {
	switch(action.type) {
		case ADD_FACE_BOX:
			return Object.assign({}, state, {box: action.payload})
		default:
			return state;
	}
}

const initialStateInput = {
	input: ''
}

export const inputStateChange = (state=initialStateInput, action={}) => {
	switch(action.type) {
		case ON_INPUT_CHANGE:
			return Object.assign({}, state, {input: action.payload})
		default:
			return state;
	}
}

const initialStateUser = {
  user: {
	    id: '',
	    name: '',
	    email: '',
	    entries: 0,
	    joined: ''
  }
}

export const userInfoUpdate = (state=initialStateUser, action={}) => {
	switch(action.type) {
		case LOAD_DATA:
			return Object.assign({}, state, {user: action.payload})
		case UPDATE_DATA: 
			console.log(state);
			console.log(state.user);
			console.log(state.user.entries);
			return Object.assign({}, state.user, {entries: action.payload});
			// return Object.assign({}, state, Object.assign({},state.user, {entries: action.payload}));
		default:
			return state;
	}
}

const initialStateImageUrl = {
	imageUrl: ''
}

export const imageUrlUpdate = (state=initialStateImageUrl, action={}) => {
	switch(action.type) {
		case URL_ENTERED:
			return Object.assign({}, state, {imageUrl: action.payload})
		default:
			return state;
	}
}
