import {
	ADD_FACE_BOX,
	ON_INPUT_CHANGE,
	LOAD_DATA,
	UPDATE_DATA,
	URL_ENTERED,
	SIGN_IN,
	ROUTE,
	DISPLAY_FLAG
} from './constants.js'

export const addFaceBox = (box) => ({
	type: ADD_FACE_BOX,
	payload: box
})

export const changeInput = (input) => ({
	type: ON_INPUT_CHANGE,
	payload: input
})

export const updateUserInfo = (data) => {
	if (typeof(data) == "object") {
		return {
			type: LOAD_DATA,
			payload: data
		}
	} else {
		return {
			type: UPDATE_DATA,
			payload: data
		}
	}
}

export const updateImageUrl = (url) => ({
	type: URL_ENTERED,
	payload: url
})

export const updateSignIn = (data) => ({
	type: SIGN_IN,
	payload: data
})

export const updateRoute = (data) => ({
	type: ROUTE,
	payload: data
})

export const updateDisplayFlag = (dataIn) => {
    if (typeof(dataIn) === "string" && dataIn !== "") {
      console.log(dataIn);
      let ext = dataIn[dataIn.length-4] + dataIn[dataIn.length-3] + dataIn[dataIn.length-2] + dataIn[dataIn.length-1];
      ext = ext.toLowerCase();
      console.log(ext);
      if (ext === ".jpg" || ext === ".png" || ext === ".bmp") {
        return {
        	type: DISPLAY_FLAG,
        	payload: true	
        }
      }
    } else {
        return {
        	type: DISPLAY_FLAG,
        	payload: false	
        }
    }
}
